// Minified codeblock plugin by Derek Webb
tinymce.PluginManager.add("codeblock",function(e,t){e.addButton("codeblock",{text:"Codeblock",icon:false,onclick:function(){win=e.windowManager.open({title:"Codeblock",body:[{type:"listbox",text:"Language",name:"codelanguage",icon:false,values:[{text:"Auto",value:"auto"},{text:"Ruby",value:"ruby"},{text:"Puppet",value:"puppet"},{text:"Bash",value:"bash"},{text:"R source",value:"rs"},{text:"R documentation",value:"rd"}],onPostRender:function(){this.value("auto")}},{type:"textbox",name:"codeblock",multiline:true,minWidth:e.getParam("code_dialog_width",600),minHeight:e.getParam("code_dialog_height",500),value:e.selection.getContent()}],onsubmit:function(t){var n="lang-"+win.find("#codelanguage").value();if(n=="lang-auto")n="";e.insertContent('<pre class="prettyprint linenums '+n+'">'+t.data.codeblock+"</pre>")}})}})})